import React, { useState, useEffect } from "react"
import { styled } from '@mui/material/styles';
import { Outlet, Link } from "react-router-dom";

import TopBar from "../../component/view/appBar";
import avatar from "../../assets/avatar/avatar.png"
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import { Table, TableBody, TableCell, TableHead, TableRow, Typography, FormControl, InputLabel, OutlinedInput, InputAdornment, IconButton } from "@mui/material";
import Visibility from '@mui/icons-material/Visibility';
import VisibilityOff from '@mui/icons-material/VisibilityOff';
import SearchIcon from '@mui/icons-material/Search';
import Paper from '@mui/material/Paper';
import AddCircleIcon from '@mui/icons-material/AddCircle';
const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));
const AddShippingComps = (props) => {
  const [formData, setFormData] = useState({
    user: {
      username: "",
      password: ""
    },
  });

  const handleChange = (e) => {
    setFormData({
      user: {
        ...formData.user,
        [e.target.name]: e.target.value,
      },
    });
  };

  return (
    <div>
      <Grid item xs={12} style={{ margin: "100px 0" }} className="bg-white">
        <Paper className="p-10">
          <Grid container spacing={2}>
            <Grid item xs={8} style={{paddingTop:"25px"}}>
              <Typography style={{ float: "left", fontWeight: "bold", fontSize: "24px" }} className="">Shipping Comps</Typography>
                <AddCircleIcon color="primary" style={{ float: "left", marginLeft: "10px", fontSize: 36 }} />
            </Grid>
            <Grid item xs={4} >
              <FormControl 
              // sx={{ m: 1, width: '25ch' }}
               variant="outlined">
                <InputLabel htmlFor="outlined-adornment-password">Password</InputLabel>
                <OutlinedInput
                  id="outlined-adornment-password"
                  // type={values.showPassword ? 'text' : 'password'}
                  // value={values.password}
                  // onChange={handleChange('password')}
                  endAdornment={
                    <InputAdornment position="start">
                      <SearchIcon />
                      {/* <IconButton
                  aria-label="toggle password visibility"
                  // onClick={handleClickShowPassword}
                  // onMouseDown={handleMouseDownPassword}
                  edge="end"
                >
                  {/* {values.showPassword ? <VisibilityOff /> : <Visibility />} 
                </IconButton> */}
                    </InputAdornment>
                  }
                  placeholder="Cari"
                />
              </FormControl>
            </Grid>
          </Grid>
          <Typography style={{ float: "left", fontWeight: "bold", fontSize: "24px" }} className="">Tambah Shipping Comps</Typography>
          <Typography style={{ float: "left", fontWeight: "bold", fontSize: "24px" }} className="">Shipping Comps</Typography>

          <label className="text-left text-gray-600 text-xl font-family:roboto font-bold">Password</label>
            <input
              name="password"
              type="password"
              value={formData.user.password}
              onChange={handleChange}
              placeholder="Password"
              className={
                "w-full p-2 text-primary border rounded-md outline-none text-sm transition duration-150 ease-in-out mb-12"
              }
            />
            <div className="flex items-center mt-3 justify-center">
              <button
                className={ 
                  "bg-blue-700 w-full max-w-md  hover:bg-blue-500 py-2 px-4 text-xl rounded-lg border border-blue focus:outline-none text-white focus:border-blacks"

                //     

                }
                value="Login"
              >
                Login
              </button>
            </div>

        </Paper>
      </Grid>


    </div>
  );
}
export default AddShippingComps
