import React, { useState, useEffect } from "react"
import TopBar from "../../component/view/appBar";

const Login = (props) => {
    const [formData, setFormData] = useState({ 
        user: {
          username: "", 
          password: "" 
        },
      });

      const handleChange = (e) => {
        setFormData({
          user: {
            ...formData.user,
            [e.target.name]: e.target.value,
          },
        });
      };

      return (
          <>
          {/* <TopBar /> */}
          <figure className="h-screen flex bg-white">
          <div className="w-full max-w-md m-auto bg-grey-300 rounded-lg border border-primaryBorder shadow-default py-10 px-1">
            <blockquote className="text-2xl font-medium text-center">
              <p className="text-lg  text-gray-600 text-xl	  font-family:roboto font-bold">Login</p>
            </blockquote>

            <div className="text-primary m-6">

          <form>
            <label className="text-left text-gray-600 text-xl	  font-family:roboto font-bold">Email</label>
            <input
              name="email"
              type="text"
              value={formData.user.email}
              onChange={handleChange}
              placeholder="Email"
              className={
                "w-full p-2 text-primary border rounded-md outline-none text-sm transition duration-150 ease-in-out mb-4"
              }
            />
            <label className="text-left text-gray-600 text-xl	  font-family:roboto font-bold">Password</label>
            <input
              name="password"
              type="password"
              value={formData.user.password}
              onChange={handleChange}
              placeholder="Password"
              className={
                "w-full p-2 text-primary border rounded-md outline-none text-sm transition duration-150 ease-in-out mb-4"
              }
            />
            <div className="flex items-center mt-3 justify-center">
              <button
                className={ 
                  "bg-blue-700 hover:bg-blue-500 py-2 px-4 text-md rounded border border-blue focus:outline-none text-white focus:border-blacks"

                //     

                }
                value="Login"
              >
                Login
              </button>
            </div>
          </form>
          <div className="flex items-center mt-3 justify-center">
          <button className={"justify-center text-blue-500 hover:underline"}>
            Need to register? Sign up for free
          </button>
          </div>
        </div>

          </div>
        </figure>

          </>
      );
  }
export default Login
