import React, { useState, useEffect } from "react"

const Login = (props) => {
    const [formData, setFormData] = useState({ 
        user: {
          username: "", 
          password: "" 
        },
      });

      const handleChange = (e) => {
        setFormData({
          user: {
            ...formData.user,
            [e.target.name]: e.target.value,
          },
        });
      };

      return (
        <figure className="h-screen flex bg-gray-100">
          <div className="w-full max-w-md m-auto bg-white rounded-lg border border-primaryBorder shadow-default py-10 px-1">
            <blockquote className="text-2xl font-medium text-center">
              <p className="text-lg font-semibold">Welcome to My-App</p>
            </blockquote>

            <div className="text-primary m-6">
          <div className="flex items-center mt-3 justify-center">
            <h1 className="text-2xl font-medium text-primary mt-4 mb-2">
              Login to your account
            </h1>
          </div>
          <form>
            <label className="text-left">Username:</label>
            <input
              name="username"
              type="text"
              value={formData.user.username}
              onChange={handleChange}
              placeholder="Username"
              className={
                "w-full p-2 text-primary border rounded-md outline-none text-sm transition duration-150 ease-in-out mb-4"
              }
            />
            <label>Password:</label>
            <input
              name="password"
              type="password"
              value={formData.user.password}
              onChange={handleChange}
              placeholder="Password"
              className={
                "w-full p-2 text-primary border rounded-md outline-none text-sm transition duration-150 ease-in-out mb-4"
              }
            />
            <div className="flex items-center mt-3 justify-center">
              <button
                className={ 
                  "bg-indigo-500 hover:bg-blue-500 py-2 px-4 text-md rounded border border-blue focus:outline-none focus:border-blacks"

                //    text-white 

                }
                value="Login"
              >
                Login
              </button>
            </div>
          </form>
          <div className="flex items-center mt-3 justify-center">
          <button className={"justify-center text-blue-500 hover:underline"}>
            Need to register? Sign up for free
          </button>
          </div>
        </div>

          </div>
        </figure>
      );
  }
export default Login
